import React, { Component } from 'react';

class Header extends Component {
  state = { button: 'none' };

  activateHome = () => {
    this.setState({ button: 'home' });
  };

  activateAbout = () => {
    this.setState({ button: 'about' });
  };

  activateServices = () => {
    this.setState({ button: 'services' });
  };

  activateContact = () => {
    this.setState({ button: 'contact' });
  };

  renderContent() {
    if (this.state.button == 'home') {
      return <h2>Home</h2>;
    } else if (this.state.button == 'about') {
      return <h2>About</h2>;
    } else if (this.state.button == 'services') {
      return <h2>Services</h2>;
    } else if (this.state.button == 'contact') {
      return <h2>Contact</h2>;
    } else return <h2>No Data</h2>;
  }

  render() {
    return (
      <div>
        <button onClick={this.activateHome}>Home</button>
        <button onClick={this.activateAbout}>About</button>
        <button onClick={this.activateServices}>Services</button>
        <button onClick={this.activateContact}>Contact</button>
        <div>The value is : {this.state.button}</div>
        {this.renderContent()}
      </div>
    );
  }
}

export default Header;
