import React, { Component } from 'react';

class App extends Component {
  state = { button: 'none' };

  activateHome = () => {
    this.setState({ button: 'home' });
  };

  activateAbout = () => {
    this.setState({ button: 'about' });
  };

  activateServices = () => {
    this.setState({ button: 'services' });
  };

  activateContact = () => {
    this.setState({ button: 'contact' });
  };

  renderContent = () => {
    return (
      <div>
        <h1>hello</h1>
      </div>
    );
  };

  render() {
    return (
      <div>
        <button onClick={this.activateHome}>Home</button>
        <button onClick={this.activateAbout}>About</button>
        <button onClick={this.activateServices}>Services</button>
        <button onClick={this.activateContact}>Contact</button>
        <div>The value is : {this.state.button}</div>
        {this.renderContent}
      </div>
    );
  }
}

export default App;
