import React, { Component } from 'react';
import Home from './Home';
import About from './About';
import Services from './Services';
import Contact from './Contact';

class Header extends Component {
  state = { button: 'home' };

  activateHome = () => {
    this.setState({ button: 'home' });
  };

  activateAbout = () => {
    this.setState({ button: 'about' });
  };

  activateServices = () => {
    this.setState({ button: 'services' });
  };

  activateContact = () => {
    this.setState({ button: 'contact' });
  };

  renderContent() {
    if (this.state.button == 'home') {
      return <Home heading="Revisin' React" />;
    } else if (this.state.button == 'about') {
      return <About />;
    } else if (this.state.button == 'services') {
      return <Services />;
    } else if (this.state.button == 'contact') {
      return <Contact />;
    } else return <Home heading="Revisin' React" />;
  }

  render() {
    return (
      <div>
        <nav class="navbar navbar-expand-lg navbar-light bg-light">
          <a class="navbar-brand" href="#">
            Navbar
          </a>
          <button
            class="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span class="navbar-toggler-icon" />
          </button>
          <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="nav navbar-nav ml-auto">
              <li class="nav-item active">
                <a class="nav-link" href="#">
                  Home <span class="sr-only">(current)</span>
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  Features
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  Pricing
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link disabled" href="#">
                  Disabled
                </a>
              </li>
            </ul>
          </div>
        </nav>
        {this.renderContent()}
      </div>
    );
  }
}

export default Header;
