import React, { Component } from 'react';

class Header extends Component {
  state = { button: 'home' };

  activateHome = () => {
    this.setState({ button: 'home' });
  };

  activateAbout = () => {
    this.setState({ button: 'about' });
  };

  activateServices = () => {
    this.setState({ button: 'services' });
  };

  activateContact = () => {
    this.setState({ button: 'contact' });
  };

  renderContent() {
    if (this.state.button == 'home') {
      return <h2>Home</h2>;
    } else if (this.state.button == 'about') {
      return <h2>About</h2>;
    } else if (this.state.button == 'services') {
      return <h2>Services</h2>;
    } else if (this.state.button == 'contact') {
      return <h2>Contact</h2>;
    } else return <h2>No Data</h2>;
  }

  render() {
    return (
      <div>
        <nav>
          <div className="nav-wrapper">
            <a href="#" classNAme="brand-logo">
              Logo
            </a>
            <ul id="nav-mobile" className="right hide-on-med-and-down">
              <li>
                <a onClick={this.activateHome}>Home</a>
              </li>
              <li>
                <a onClick={this.activateAbout}>About</a>
              </li>
              <li>
                <a onClick={this.activateServices}>Services</a>
              </li>
              <li>
                <a onClick={this.activateContact}>Contact</a>
              </li>
            </ul>
          </div>
        </nav>
        <div>The value is : {this.state.button}</div>
        {this.renderContent()}
      </div>
    );
  }
}

export default Header;
